	public List<String> readOrg(){
		List<String> ls = new ArrayList<String>();
 
		try {
			//读取存放地址的文件
			File f = new File("Data/data38z.txt");			
			if (f.isFile() && f.exists()) {
				//按行读取文件中的地址
				InputStreamReader isr = new InputStreamReader(
						new FileInputStream(f), "UTF-8");
				BufferedReader br = new BufferedReader(isr);
				String lineStr = br.readLine();
				while (lineStr != null) { 
					ls.add(lineStr);
					lineStr = br.readLine();
				}
				br.close();
				isr.close();
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
		return ls;
	}
	
	//比较算法
	public void test(List<String> ls){
		List<String> rsls = new ArrayList<String>();
		String s = "";
		String response = "";
		int maxlen=5;
		int k=0;int j=maxlen;int slen=maxlen;
		
		try {
			File f = new File("Data/FMMresult.txt");
			if (!f.exists()){
				f.createNewFile();
			}
			OutputStreamWriter result = new  OutputStreamWriter(new FileOutputStream(f),"UTF-8"); 
			BufferedWriter write = new BufferedWriter(result);
			
			for(int i=0;i<ls.size();i++){
				//System.out.print(ls.get(i)+" :: ");
				//System.out.println(ls.get(i));
				write.write(ls.get(i)+" :: ");
				if (slen>=ls.get(i).length()){
					slen=ls.get(i).length();
					j = slen;
				}else {
					j=maxlen;
				}
				k=0;
				while (j>0) {
					if(k >= ls.get(i).length()){
						break;
					}
		            if ((k+j)>=ls.get(i).length()) {
		            	s += ls.get(i).substring(k,ls.get(i).length());
		            } else {
					s += ls.get(i).substring(k,k+j);  
		            }
		            //System.out.println("now S = " + s);
		            if(isExist(s) ) { 
		                response += (s + "|");  
		                s = ""; 
		                k=k+j;
		                j=maxlen;
		                //System.out.println(j);
		            }else if (s.length()==1){  
		            	//response += (s + "|");  
		                s = "";
		                k++;
		                j=maxlen;
		                //System.out.println(j);
		            }else{
		            	s = "";
		            	j--;
		            	//System.out.println(j);
		            }
		            
				}
				write.write(response + "\n");
	            rsls.add(response);
	          
	            response="";
			}
			write.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
	}
	
	
